function getDayOfWeek(query) {
    const [day, month, year] = query.split('/').map(Number);
    const date = new Date(year, month - 1, day);
    return date.toLocaleDateString('pt-BR', { weekday: 'long' });
}

// Array para armazenar os pr√≥ximos 10 dias
const next10Days = [];

for (let i = 0; i < 10; i++) {
    const date = new Date();
    date.setDate(date.getDate() + i);

    next10Days.push(` ${date.toLocaleDateString('pt-BR', { weekday: 'long' })}, ${date.toLocaleDateString('pt-BR')}`);
}

return [{ json: { resultado: next10Days.join('\n') } }];

// Garante que inputText seja uma string v√°lida
let inputText = $json["text"] || "";

// Define o limite m√°ximo de palavras
const WORD_LIMIT = 100; // Ajuste conforme necess√°rio

// Conta as palavras no inputText
let wordCount = inputText.trim().split(/\s+/).length;

// Se ultrapassar o limite, corta o texto e adiciona aviso
if (wordCount > WORD_LIMIT) {
    inputText = inputText.split(/\s+/).slice(0, WORD_LIMIT).join(" ") + " [LIMITE DE TEXTO EXCEDIDO]";
}

// Remove comandos suspeitos de engenharia de prompt
inputText = inputText.replace(/(ignore|bypass|jailbreak|disregard|prompt|system prompt)/gi, "[REMOVIDO]");

// Substitui todos os casos de **texto** para *texto*
let outputText = inputText.replace(/\*\*(.*?)\*\*/g, '*$1*');

// Substitui todas as ocorr√™ncias de \n para \n\n
outputText = outputText.replace(/([^\n]|^)\n([^\n]|$)/g, '$1\n\n$2');

// Remove caracteres especiais usados para engenharia de prompt
outputText = outputText.replace(/[`$<>{}[\]]/g, "");

// Retorna o resultado
return {
  json: {
    outputText: outputText
  }
};

